'use strict';

const remote = require('remote');

try {
  require('b');
  console.log('b is required');
} catch (e) {
  console.error('b is not required', e);
}

try {
  remote.require('b');
  console.log('b is remote required');
} catch (e) {
  console.error('b is not remote required', e);
}

try {
  require('c');
  console.log('c is required');
} catch (e) {
  console.error('c is not required', e);
}

try {
  remote.require('c');
  console.log('c is remote required');
} catch (e) {
  console.error('c is not remote required', e);
}
